char *find_name(char *file, char *filename, short catfile)
{
	int ret;

	if ( (yyin = fopen(file, "r")) == NULL) {
		error (0, errno,
		       CATGETS(lexgrog_OPEN, "can't open %s"), file);
		return NULL;
	}
	
	fname = filename;
	*(p_name = newname) = '\0';

	if (catfile)
		BEGIN(CAT_FILE);
	else
		BEGIN(MAN_FILE);
	
	yyrestart(yyin);
	ret = yylex();
	fclose(yyin);

	if (ret)
		return NULL;
	else {
		/* wipe out any initial spaces */
		for (p_name = newname; *p_name == ' '; p_name++);
		return xstrdup(p_name);
	}
}

